package kg.geektech.kotlin2

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.viewbinding.ViewBinding
import kg.geektech.kotlin2.databinding.ActivityMainBinding
import kg.geektech.kotlin2.extentions.glide
import kg.geektech.kotlin2.extentions.showToast
import kotlin.random.Random

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    private val image = mutableListOf(
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBdhcpSc0WjER50ClbbtWoM8vBkRUTPUJ_oA&usqp=CAU",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS2WpPI8_RkuxG-7IaB14X4BYQ-781RgUxrBQ&usqp=CAU")
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        binding.btnSubmit.setOnClickListener {
            submit()
        }
        binding.btnRandom.setOnClickListener {
            randomImage()
        }
    }

    private fun randomImage() {
      val random = Random.nextInt(image.size)
        val randomImage = image[random]
        binding.urlImage.glide(randomImage)
    }

    private fun submit() {
        val submit = binding.urlLink.text.toString()
        if (submit.isEmpty()){
            showToast(getString(R.string.toast))
        }else{
            image.add(submit)
            binding.urlLink.setText("")
        }
    }
}